import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      currLink: '',
      botName: ''
    }
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }
  render() {
    return (
      <div className='app'>
        <header>
            <div className='wrapper'>
              <h1>Zoom Google Assistant</h1>

            </div>
        </header>
        <div className='container'>
          <section className='add-bot'>
              <form>
                <input type="text" name="Bot Name" placeholder="What's your bot's name?" onChange={this.handleChange} value={this.state.botName} />
                <input type="text" name="currentItem" placeholder="URL of Zoom Meeting:" onChange={this.handleChange} value={this.state.currLink} />  
                <button>Add Bot</button>
              </form>
          </section>
          <section className='display-bot'>
            <div className='wrapper'>
              <ul>
              </ul>
            </div>
          </section>
        </div>
      </div>
    );
  }
}
export default App;
